package edu.vcentry.seljava.class04;

public class StringPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char myRupee = '\u20B9';
		double totalPrice = 99.99;
		System.out.println(myRupee + " " + totalPrice );
		
		String newString = "New String...";
		
		System.out.println("My New String is:" + newString);
		
		String oldString = newString + "I am no more new...";
		
		System.out.println(oldString);
		
		int myNew = 3 + 4;
		System.out.println(myNew);

	}

}
